'use client'

import { Check } from 'lucide-react'
import { css, Global } from '@emotion/react'
import { Button } from '@/components/ui/button'
import { Card, CardContent, CardFooter, CardHeader } from '@/components/ui/card'

interface PricingTierProps {
  name: string
  price: number
  period?: string
  features: string[]
  primaryColor?: string
  secondaryColor?: string
  accentColor?: string
  buttonText?: string
  popular?: boolean
  onSubscribe?: () => void
}

export default function PricingTier({
  name = 'Basic',
  price = 10,
  period = 'month',
  features = ['Feature 1', 'Feature 2'],
  primaryColor = '#2563eb',
  secondaryColor = '#1d4ed8',
  accentColor = '#dbeafe',
  buttonText = 'Subscribe',
  popular = false,
  onSubscribe = () => {},
}: PricingTierProps) {
  return (
    <>
      <Global
        styles={css`
          .pricing-tier-animation {
            transition: transform 0.2s ease-in-out;
          }
          .pricing-tier-animation:hover {
            transform: translateY(-4px);
          }
          .feature-animation {
            transition: background-color 0.2s ease-in-out;
          }
          .feature-animation:hover {
            background-color: ${accentColor};
          }
        `}
      />
      <Card
        css={css`
          position: relative;
          width: 100%;
          max-width: 400px;
          border: ${popular ? `2px solid ${primaryColor}` : '1px solid #e5e7eb'};
          overflow: hidden;
        `}
        className="pricing-tier-animation"
      >
        {popular && (
          <div
            css={css`
              position: absolute;
              top: 12px;
              right: -32px;
              background-color: ${primaryColor};
              color: white;
              padding: 4px 40px;
              transform: rotate(45deg);
              font-size: 0.875rem;
              font-weight: 500;
            `}
          >
            Popular
          </div>
        )}
        <CardHeader
          css={css`
            text-align: center;
            padding: 2rem;
            background: linear-gradient(to bottom right, ${primaryColor}, ${secondaryColor});
            color: white;
          `}
        >
          <h3 className="text-lg font-semibold">{name}</h3>
          <div className="flex items-baseline justify-center mt-4">
            <span className="text-4xl font-bold">${price}</span>
            <span className="ml-1 text-sm">/{period}</span>
          </div>
        </CardHeader>
        <CardContent
          css={css`
            padding: 2rem;
          `}
        >
          <ul className="space-y-3">
            {features.map((feature, index) => (
              <li
                key={index}
                className="flex items-center space-x-3 feature-animation rounded-lg p-2"
                css={css`
                  transition: transform 0.2s ease-in-out;
                  &:hover {
                    transform: translateX(4px);
                  }
                `}
              >
                <Check
                  className="flex-shrink-0"
                  css={css`
                    color: ${primaryColor};
                  `}
                />
                <span className="text-gray-600">{feature}</span>
              </li>
            ))}
          </ul>
        </CardContent>
        <CardFooter
          css={css`
            padding: 2rem;
            background-color: #f9fafb;
          `}
        >
          <Button
            className="w-full"
            css={css`
              background-color: ${primaryColor};
              &:hover {
                background-color: ${secondaryColor};
              }
            `}
            onClick={onSubscribe}
          >
            {buttonText}
          </Button>
        </CardFooter>
      </Card>
    </>
  )
}
